func (o *Invoice) SetCompanyClient(ctx context.Context, exec boil.ContextExecutor, insert bool, related *CompanyClient) error {
	var err error
	if insert {
		if err = related.Insert(ctx, exec, boil.Infer()); err != nil {
			return errors.Wrap(err, "failed to insert into foreign table")
		}
	}

	updateQuery := fmt.Sprintf(
		"UPDATE `invoice` SET %s WHERE %s",
		strmangle.SetParamNames("`", "`", 0, []string{"company_client_id"}),
		strmangle.WhereClause("`", "`", 0, invoicePrimaryKeyColumns),
	)
	values := []interface{}{related.ID, o.ID}

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, updateQuery)
		fmt.Fprintln(writer, values)
	}
	if _, err = exec.ExecContext(ctx, updateQuery, values...); err != nil {
		return errors.Wrap(err, "failed to update local table")
	}

	o.CompanyClientID = related.ID
	if o.R == nil {
		o.R = &invoiceR{
			CompanyClient: related,
		}
	} else {
		o.R.CompanyClient = related
	}

	if related.R == nil {
		related.R = &companyClientR{
			Invoices: InvoiceSlice{o},
		}
	} else {
		related.R.Invoices = append(related.R.Invoices, o)
	}

	return nil
}

